INSERT INTO `gossip_menu_option` (`menu_id`, `id`, `option_icon`, `option_text`, `option_id`, `npc_option_npcflag`, `action_menu_id`, `action_poi_id`, `box_coded`, `box_money`, `box_text`) VALUES
(11461, 0, 0, "Why are you stealing eggs?", 1, 1, 11462, 0, 0, 0, ''),
(11462, 0, 0, "Who? Who are you giving the eggs to?", 1, 1, 11463, 0, 0, 0, ''),
(11463, 0, 0, "Brood of the Earth-Warder... you answer to a black dragon? Give me a name!", 1, 1, 11464, 0, 0, 0, '');
